<?php namespace Illuminate\Console; use Symfony\Component\Console\Input\ArrayInput; use Symfony\Component\Console\Output\NullOutput; use Symfony\Component\Console\Input\InputInterface; use Symfony\Component\Console\Output\OutputInterface; class Command extends \Symfony\Component\Console\Command\Command { protected $laravel; protected $input; protected $output; protected $name; protected $description; public function __construct() { parent::__construct($this->name); $this->setDescription($this->description); $this->specifyParameters(); } protected function specifyParameters() { foreach ($this->getArguments() as $arguments) { call_user_func_array(array($this, 'addArgument'), $arguments); } foreach ($this->getOptions() as $options) { call_user_func_array(array($this, 'addOption'), $options); } } public function run(InputInterface $input, OutputInterface $output) { $this->input = $input; $this->output = $output; return parent::run($input, $output); } protected function execute(InputInterface $input, OutputInterface $output) { return $this->fire(); } public function call($command, array $arguments = array()) { $instance = $this->getApplication()->find($command); $arguments['command'] = $command; return $instance->run(new ArrayInput($arguments), $this->output); } public function callSilent($command, array $arguments = array()) { $instance = $this->getApplication()->find($command); $arguments['command'] = $command; return $instance->run(new ArrayInput($arguments), new NullOutput); } public function argument($key = null) { if (is_null($key)) return $this->input->getArguments(); return $this->input->getArgument($key); } public function option($key = null) { if (is_null($key)) return $this->input->getOptions(); return $this->input->getOption($key); } public function confirm($question, $default = true) { $dialog = $this->getHelperSet()->get('dialog'); return $dialog->askConfirmation($this->output, "<question>$question</question>", $default); } public function ask($question, $default = null) { $dialog = $this->getHelperSet()->get('dialog'); return $dialog->ask($this->output, "<question>$question</question>", $default); } public function secret($question, $fallback = true) { $dialog = $this->getHelperSet()->get('dialog'); return $dialog->askHiddenResponse($this->output, "<question>$question</question>", $fallback); } public function choice($question, array $choices, $default = null, $attempts = false) { $dialog = $this->getHelperSet()->get('dialog'); return $dialog->select($this->output, "<question>$question</question>", $choices, $default, $attempts); } public function line($string) { $this->output->writeln($string); } public function info($string) { $this->output->writeln("<info>$string</info>"); } public function comment($string) { $this->output->writeln("<comment>$string</comment>"); } public function question($string) { $this->output->writeln("<question>$string</question>"); } public function error($string) { $this->output->writeln("<error>$string</error>"); } protected function getArguments() { return array(); } protected function getOptions() { return array(); } public function getOutput() { return $this->output; } public function getLaravel() { return $this->laravel; } public function setLaravel($laravel) { $this->laravel = $laravel; } }
